import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styleUrls: ['./book-list.component.css']
})
export class BookListComponent implements OnInit {
  books: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.getBooks();
  }

  getBooks() {
    this.http.get<any[]>('http://localhost:3000/books')
      .subscribe(books => {
        this.books = books;
      });
  }

  deleteBook(bookId: string) {
    this.http.delete<any>(`http://localhost:3000/books/${bookId}`)
      .subscribe(() => {
        // Действия после успешного удаления книги, например, перезагрузка списка книг
        this.getBooks(); // Перезагрузка списка книг
      });
  }

}

<div class="row">

  <div class="col-sm card mb-3 mx-2 d-flex" *ngFor="let book of books">
    <div class="card-body">
      <h5 class="card-title">{{ book.title }}</h5>
      <hr>

      <p class="card-text">by {{ book.author }}</p>
      <!-- Кнопка удаления книги -->

      <button class="btn btn-danger mr-2 me-4 my-2" (click)="deleteBook(book._id)">Delete</button>
      <a class="btn btn-primary" [routerLink]="['/books', book._id]">Details</a>

    </div>
  </div>
</div>
