import { Component, OnInit } from '@angular/core';
import { CheckFormService } from '../check-form.service';
import { AddTaskService } from '../add-task.service';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';


@Component({
  selector: 'app-addtaskform',
  templateUrl: './addtaskform.component.html',
  styleUrls: ['./addtaskform.component.css']
})
export class AddtaskformComponent implements OnInit {

  name: string;
  description: string;

  constructor(
    private checkForm: CheckFormService,
    private toastr: ToastrService,
    private router: Router,
    private addtask: AddTaskService,
    ){
    this.name = '';
    this.description = '';
  }
  ngOnInit(): void {  
  }

  addTaskClick(){
    if (this.checkForm.checkDescription(this.description)) {
      const task = {
        name: this.name,
        description: this.description,
      } 
      console.log('success');

      this.addtask.addTask(task).subscribe(
        (data: any) => {
          console.log(data);
          if (!data.success) {
            this.toastr.error(data.msg, '');
          } else {
            this.toastr.success(data.msg, '');
          }
        },
        (error: any) => {
          console.error('Ошибка при выполнении запроса:', error);``
        }
      );


    } else {
      this.toastr.error('Field "Description" is required', '');
      return false;
    }

    return false
  }
}
