import {Component} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import Chart from 'chart.js/auto';

@Component({
  selector: 'app-file-upload',
  templateUrl: './file-upload.component.html',
  styleUrls: ['./file-upload.component.css']
})
export class FileUploadComponent {
  selectedFile: File | undefined;
  analysisData: any = {};
  chartInstance: Chart | null = null;

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  onFileUpload() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);

      this.http.post<any>('http://localhost:3000/upload', formData).subscribe({
        next: response => {
          console.log('Ответ сервера:', response);
          this.displayAnalysis(response); // Отображение анализа
        },
        error: error => {
          console.error('Ошибка загрузки файла', error);
        }
      });
    }
  }


  displayAnalysis(analysisData: any) {
    this.analysisData = analysisData;

    // Проверка на наличие данных для графика
    if (this.analysisData.labels && this.analysisData.values) {
      const canvas: any = document.getElementById('chartCanvas'); // Получение элемента canvas из шаблона

      // Уничтожение предыдущего графика, если он существует
      if (this.chartInstance) {
        this.chartInstance.destroy();
      }

      const ctx = canvas.getContext('2d');

      // Создание экземпляра графика с использованием Chart.js
      this.chartInstance = new Chart(ctx, {
        type: 'bar', // Тип графика (можно выбрать другой тип по вашему желанию)
        data: {
          labels: this.analysisData.labels,
          datasets: [{
            label: 'Анализ данных',
            data: this.analysisData.values,
            backgroundColor: 'rgba(54, 162, 235, 0.6)', // Цвет фона столбцов
            borderColor: 'rgba(54, 162, 235, 1)', // Цвет обводки столбцов
            borderWidth: 1
          }]
        },
        options: {
          scales: {
            y: {
              beginAtZero: true
            }
          }
        }
      });
    }
  }
}
<form (ngSubmit)="onFileUpload()" enctype="multipart/form-data">
  <input type="file" (change)="onFileSelected($event)">
  <button type="submit">Загрузить файл</button>
</form>


<div *ngIf="analysisData.labels">
<!--  <p>Пример вывода данных:</p>-->
<!--  <p>Метки: {{ analysisData.labels }}</p>-->

  <div>
    <!-- Элемент для отображения графика -->
    <canvas id="chartCanvas" width="400" height="400"></canvas>
  </div>
  <!-- Здесь можно добавить элементы для отображения графика -->
</div>
