import { Component } from '@angular/core';
import { GoogleBooksService } from '../google-books.service';
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
@Component({
  selector: 'app-book-search',
  templateUrl: './book-search.component.html',
  styleUrls: ['./book-search.component.css']
})
export class BookSearchComponent {
  books: any[] = [];
  query: string = '';

  constructor(private http: HttpClient, private googleBooksService: GoogleBooksService) {}


  getBestsellers(): Observable<any> {
    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.apiKey}`);
    return this.http.get<any>(`${this.apiUrl}/volumes?q=bestsellers`, { headers: headers });
  }

  searchBooks() {
    this.googleBooksService.searchBooks(this.query).subscribe(data => {
      this.books = data.items;
    });
  }
}
<div>
  <input type="text" [(ngModel)]="query" placeholder="Введите запрос">
  <button (click)="searchBooks()">Найти книги</button>
</div>
<div *ngIf="books && books.length > 0">
  <div *ngFor="let book of books">
    <h3>{{ book.volumeInfo.title }}</h3>
    <p>{{ book.volumeInfo.authors }}</p>
    <p>{{ book.volumeInfo.description }}</p>
  </div>
</div>
