import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-book-details',
  templateUrl: './book-details.component.html',
  styleUrls: ['./book-details.component.css']
})
export class BookDetailsComponent implements OnInit {
  bookId: string;
  bookDetails: any;

  constructor(private route: ActivatedRoute, private http: HttpClient) {
    this.bookId = ''; // Начальное значение bookId
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.bookId = params.get('id') || '';
      this.getBookDetails();
    });
  }

  getBookDetails() {
    this.http.get<any>(`http://localhost:3000/books/${this.bookId}`)
      .subscribe(book => {
        this.bookDetails = book;
      });
  }
}
<div *ngIf="bookDetails" class="container mt-4">
  <div class="card shadow border-0">
    <div class="card-body">
      <h2 class="card-title h2 text-center border-bottom pb-3">{{ bookDetails.title }}</h2>
      <p class="card-text fs-4"><b>Author:</b> {{ bookDetails.author }}</p>
      <p class="card-text"><b>Description:</b> {{ bookDetails.description }}</p>
      <!-- Кнопка обновления книги -->
      <a class="btn btn-primary" [routerLink]="['/edit', bookId]">Update</a>
    </div>
  </div>
</div>
