import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GoogleBooksService } from '../google-books.service';

@Component({
  selector: 'app-google-book-details',
  templateUrl: './google-book-details.component.html',
  styleUrls: ['./google-book-details.component.css']
})
export class GoogleBookDetailsComponent implements OnInit {
  bookId: string = '';
  bookDetails: any = {};

  constructor(private route: ActivatedRoute, private googleBooksService: GoogleBooksService) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.bookId = params['id'];
      this.loadBookDetails(this.bookId);
    });
  }

  loadBookDetails(id: string) {
    this.googleBooksService.getBookDetails(id).subscribe(data => {
      this.bookDetails = data;
    });
  }
}
<div class="container">
  <div class="row">
      <div class="card">
        <div class="row g-0">
          <div class="col-md-4">
            <img [src]="bookDetails.volumeInfo.imageLinks?.thumbnail" class="img-fluid rounded-start" alt="Book Cover">
          </div>
          <div class="col-md-8">
            <div class="card-body">
              <h5 class="card-title">{{ bookDetails.volumeInfo.title }}</h5>
              <p class="card-text">Автор: {{ bookDetails.volumeInfo.authors }}</p>
              <p class="card-text">Год выпуска: {{ bookDetails.volumeInfo.publishedDate }}</p>
              <p class="card-text">Страниц: {{ bookDetails.volumeInfo.pageCount }}</p>
              <p class="card-text">Категория: {{ bookDetails.volumeInfo.categories }}</p>
              <p class="card-text">{{ bookDetails.volumeInfo.description }}</p>
              <a [href]="bookDetails.volumeInfo.previewLink" target="_blank" class="btn btn-primary">Перейти в Google Books</a>
            </div>
          </div>
        </div>
    </div>
  </div>
</div>
