import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BookService } from '../book.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css']
})
export class AddBookComponent {
  newBook = {
    title: '',
    author: '',
    description: ''
  };

  constructor(private http: HttpClient, private bookService: BookService , private toastr: ToastrService) { }

  addBook() {
    this.http.post<any>('http://localhost:3000/books', this.newBook)
      .subscribe({
        next: (response) => {
          this.toastr.success('Книга успешно добавлена', 'Успешно');
          this.clearForm();
        },
        error: (error) => {
          this.toastr.error('Ошибка при добавлении книги', 'Ошибка');
        }
      });
  }

  clearForm() {
    this.newBook = {
      title: '',
      author: '',
      description: ''
    };
  };
}
<form class="container mt-4" (submit)="addBook()">
  <div class="mb-3">
    <label for="title" class="form-label">Title</label>
    <input type="text" class="form-control" id="title" placeholder="Title" [(ngModel)]="newBook.title" name="title">
  </div>
  <div class="mb-3">
    <label for="author" class="form-label">Author</label>
    <input type="text" class="form-control" id="author" placeholder="Author" [(ngModel)]="newBook.author" name="author">
  </div>
  <div class="mb-3">
    <label for="description" class="form-label">Description</label>
    <textarea class="form-control" id="description" placeholder="Description" [(ngModel)]="newBook.description" name="description"></textarea>
  </div>
  <button type="submit" class="btn btn-primary">Add Book</button>
</form>
