import { Component, OnInit } from '@angular/core';
import { CheckFormService } from '../check-form.service';
import { ToastrService } from 'ngx-toastr';


@Component({
  selector: 'app-addtaskform',
  templateUrl: './addtaskform.component.html',
  styleUrls: ['./addtaskform.component.css']
})
export class AddtaskformComponent implements OnInit {

  name: string;
  description: string;

  constructor(
    private checkForm: CheckFormService,
    private flashMessages: FlashMessagesService,
    ){
    this.name = '';
    this.description = '';
  }
  ngOnInit(): void {  
  }

  addTaskClick(){
    if (this.checkForm.checkDescription(this.description)) {
      const task = {
        name: this.name,
        description: this.description,
      } 
      console.log('success');

      return false
    } else {
      this.flashMessages.show("Field 'description' is required", {
        cssClass: 'alert-danger',
        timeout: 5000
      });
      return false;
    }
  }
}
