import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css']
})
export class AddBookComponent {
  newBook = {
    title: '',
    author: '',
    description: ''
  };

  constructor(private http: HttpClient) { }

  addBook() {
    this.http.post<any>('http://localhost:3000/books', this.newBook)
      .subscribe(() => {
        // Действия после успешного добавления книги, например, перезагрузка списка книг
      });
  }
}
<!-- Форма для добавления новой книги -->
<form (submit)="addBook()">
  <input type="text" placeholder="Title" [(ngModel)]="newBook.title">
  <input type="text" placeholder="Author" [(ngModel)]="newBook.author">
  <textarea placeholder="Description" [(ngModel)]="newBook.description"></textarea>
  <!-- другие поля книги... -->
  <button type="submit">Add Book</button>
</form>
