import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-file-upload',
  templateUrl: './file-upload.component.html',
  styleUrls: ['./file-upload.component.css']
})
export class FileUploadComponent {
  selectedFile: File | undefined;
  analysisData: any = {};

  constructor(private http: HttpClient) {}

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  onFileUpload() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);

      this.http.post<any>('http://localhost:3000/upload', formData).subscribe({
        next: response => {
          console.log('Ответ сервера:', response);
          this.displayAnalysis(response); // Отображение анализа
        },
        error: error => {
          console.error('Ошибка загрузки файла', error);
        }
      });
    }
  }

  displayAnalysis(analysisData: any) {
    this.analysisData = analysisData; // Присваиваем данные для использования в шаблоне
    // Здесь можно использовать данные для визуализации, например, с помощью библиотеки Chart.js
    // Например, если есть данные для графика, можно построить его здесь
  }
}
<form (ngSubmit)="onFileUpload()" enctype="multipart/form-data">
  <input type="file" (change)="onFileSelected($event)">
  <button type="submit">Загрузить файл</button>
</form>
