import { Injectable } from '@angular/core';
import { Http, Headers } from '@angular/http';
import 'rxjs/add/operator/map';

@Injectable({
  providedIn: 'root'
})
export class AddTaskService {

  constructor(private http: Http) { }

  addTask(task: any) {
    let headers = new Headers();
    headers.append('Content-Type', 'application/json'); // Заменено 'application-json' на 'application/json'

    return this.http.post('http://localhost:3000/task/addtask', JSON.stringify(task), { headers: headers })
      .map(res => res.json());
  }
}
