import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-edit-book',
  templateUrl: './edit-book.component.html',
  styleUrls: ['./edit-book.component.css']
})
export class EditBookComponent implements OnInit {
  bookId: string | null = null; // Используем тип string | null

  updatedBook = {
    title: '',
    author: '',
    description: ''
    // другие поля книги...
  };

  constructor(private route: ActivatedRoute, private http: HttpClient) { }

  ngOnInit(): void {
    this.bookId = this.route.snapshot.paramMap.get('id');
    if (this.bookId) {
      // Получение информации о книге для редактирования
      this.http.get<any>(`http://localhost:3000/books/${this.bookId}`)
        .subscribe(book => {
          this.updatedBook = book;
        });
    }
  }

  updateBook() {
    if (this.bookId) {
      this.http.put<any>(`http://localhost:3000/books/${this.bookId}`, this.updatedBook)
        .subscribe(() => {
          // Действия после успешного обновления книги, например, перезагрузка списка книг
        });
    }
  }
}
<!-- Форма для редактирования книги -->
<form (submit)="updateBook()">
  <input type="text" placeholder="Title" [(ngModel)]="updatedBook.title">
  <input type="text" placeholder="Author" [(ngModel)]="updatedBook.author">
  <textarea placeholder="Description" [(ngModel)]="updatedBook.description"></textarea>
  <!-- другие поля книги... -->
  <button type="submit">Update Book</button>
</form>
