import {Component} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import Chart from 'chart.js/auto';

@Component({
  selector: 'app-file-upload',
  templateUrl: './file-upload.component.html',
  styleUrls: ['./file-upload.component.css']
})
export class FileUploadComponent {
  selectedFile: File | undefined;
  analysisData: any = {};
  chartInstance: Chart | null = null;

  constructor(private http: HttpClient) {
  }

  onFileSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  onFileUpload() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);

      this.http.post<any>('http://localhost:3000/upload', formData).subscribe({
        next: response => {
          console.log('Ответ сервера:', response);
          this.displayBarChart(response); // Отображение анализа
          this.displayPieChart(response); // Отображение анализа
        },
        error: error => {
          console.error('Ошибка загрузки файла', error);
        }
      });
    }
  }


  displayBarChart(analysisData: any) {
    this.analysisData = analysisData;

    console.log(Object.keys(this.analysisData.numericColumns))

    const chartData = {
      labels: Object.keys(this.analysisData.numericColumns),
      datasets: [{
        label: 'Значения',
        data: this.analysisData.numericColumns,
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      }]
    };

    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      // Дополнительные опции для столбчатой диаграммы
    };

    const ctx = document.getElementById('chartBarCanvas') as HTMLCanvasElement;
    this.chartInstance = new Chart(ctx, {
      type: 'bar',
      data: chartData,
      options: chartOptions,
    });
  }

  displayPieChart(analysisData: any) {
    this.analysisData = analysisData;

    const chartData = {
      labels: Object.keys(this.analysisData.numericColumns),
      datasets: [{
        data: this.analysisData.numericColumns,
        backgroundColor: [
          'rgba(255, 99, 132, 0.5)',
          'rgba(54, 162, 235, 0.5)',
          'rgba(255, 206, 86, 0.5)',
          // Добавьте дополнительные цвета при необходимости
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          // Добавьте дополнительные цвета при необходимости
        ],
        borderWidth: 1,
      }]
    };

    const chartOptions = {
      responsive: true,
      maintainAspectRatio: false,
      // Дополнительные опции для круговой диаграммы
    };

    const ctx = document.getElementById('chartPieCanvas') as HTMLCanvasElement;
    const pieChart = new Chart(ctx, {
      type: 'pie',
      data: chartData,
      options: chartOptions,
    });
  }

}
