import { Component } from '@angular/core';
import { GoogleBooksService } from '../google-books.service';
import { Router } from '@angular/router';


import { FormControl } from '@angular/forms';
import { catchError, filter, switchMap, tap } from 'rxjs/operators';
import { EMPTY } from 'rxjs';


@Component({
  selector: 'app-book-search',
  templateUrl: './book-search.component.html',
  styleUrls: ['./book-search.component.css']
})
export class BookSearchComponent {
  books: any[] = [];
  bestsellers: any[] = [];

  searchControl = new FormControl('');


  constructor(private router: Router, private googleBooksService: GoogleBooksService)  {
    this.searchControl.valueChanges.pipe(
      filter(query => !!query && query.trim().length > 0), // Фильтрация пустых запросов
      switchMap(query => {
        return this.googleBooksService.searchBooks(query).pipe(
          catchError(() => EMPTY) // Обработка ошибок и возвращение пустого потока
        );
      })
    ).subscribe((data: any) => {
      this.books = data.items || []; // Обновление списка книг при изменении результатов поиска
    });
  }

  // searchBooks() {
  //   this.googleBooksService.searchBooks(this.query).subscribe(data => {
  //     this.books = data.items;
  //   });
  // }

  ngOnInit() {
    this.googleBooksService.getBestsellers().subscribe(data => {
      console.log(data.items)
      this.bestsellers = data.items;
    });
  }

  viewBookDetails(bookId: string) {
    this.router.navigate(['/googleBooks', bookId]);
  }


}
<div class="container mb-5 shadow">
  <div class="row border rounded p-4">
    <div class="text-uppercase lead fs-2 text-center">find books from google books api</div>
    <div class="col-md-12 d-flex mt-2">
      <input type="text" class="form-control" [formControl]="searchControl" placeholder="Введите запрос">
    </div>



    <div class="container mt-4">
      <div class="row">
        <div class="col-md-3 mb-4" *ngFor="let book of books">
          <div class="card h-100" (click)="viewBookDetails(book.id)">
            <img [src]="book.volumeInfo.imageLinks?.thumbnail" class="card-img-top book-cover" alt="Book Cover">
            <div class="card-body">
              <h5 class="card-title">{{ book.volumeInfo.title }}</h5>
              <p class="card-text">Автор: {{ book.volumeInfo.authors }}</p>
              <p class="card-text">Страниц: {{ book.volumeInfo.pageCount }}</p>
              <p class="card-text">Год выпуска: {{ book.volumeInfo.publishedDate }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>



<div class="container mb-5 shadow">
  <div class="row border rounded p-4">
    <div class="text-uppercase fs-2 lead text-center">Bestsellers from google books api</div>
    <div class="container mt-4">
      <div class="row">
        <div class="col-lg-3 mb-4" *ngFor="let bestseller of bestsellers">
          <div class="card h-100" (click)="viewBookDetails(bestseller.id)">
            <img [src]="bestseller.volumeInfo.imageLinks?.thumbnail" class="card-img-top book-cover" alt="Book Cover">
            <div class="card-body">
              <h5 class="card-title">{{ bestseller.volumeInfo.title }}</h5>
              <p class="card-text">Автор: {{ bestseller.volumeInfo.authors }}</p>
              <p class="card-text" *ngIf="bestseller.volumeInfo.pageCount">{{ bestseller.volumeInfo.pageCount }} страниц</p>
              <p class="card-text">Дата выпуска: {{ bestseller.volumeInfo.publishedDate }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>
