import { Component } from '@angular/core';
import { GoogleBooksService } from '../google-books.service';

@Component({
  selector: 'app-book-search',
  templateUrl: './book-search.component.html',
  styleUrls: ['./book-search.component.css']
})
export class BookSearchComponent {
  books: any[] = [];
  query: string = '';
  bestsellers: any[] = [];


  constructor(private googleBooksService: GoogleBooksService) {}

  searchBooks() {
    this.googleBooksService.searchBooks(this.query).subscribe(data => {
      this.books = data.items;
    });
  }

  ngOnInit() {
    this.googleBooksService.getBestsellers().subscribe(data => {
      console.log(data.items)
      this.bestsellers = data.items;
    });
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <input type="text" class="form-control" [(ngModel)]="query" placeholder="Введите запрос">
      <button class="btn btn-primary mt-2" (click)="searchBooks()">Найти книги</button>
    </div>
  </div>

  <div *ngIf="books && books.length > 0" class="mt-4">
    <div *ngFor="let book of books" class="card mb-3">
      <div class="card-body">
        <h3 class="card-title">{{ book.volumeInfo.title }}</h3>
        <p class="card-text">{{ book.volumeInfo.authors }}</p>
        <p class="card-text">{{ book.volumeInfo.description }}</p>
      </div>
    </div>
  </div>

  <div *ngIf="bestsellers && bestsellers.length > 0" class="mt-4">
    <div *ngFor="let book of bestsellers" class="card mb-3">
      <div class="card-body">
        <h3 class="card-title">{{ book.title }}</h3>
        <p class="card-text">{{ book.volumeInfo.authors }}</p>
        <p class="card-text">{{ book.volumeInfo.description }}</p>
        <!-- Другая информация о книге -->
      </div>
    </div>
  </div>
</div>
