import { Component } from '@angular/core';
import { GoogleBooksService } from '../google-books.service';

@Component({
  selector: 'app-book-search',
  templateUrl: './book-search.component.html',
  styleUrls: ['./book-search.component.css']
})
export class BookSearchComponent {
  books: any[] = [];
  query: string = '';
  bestsellers: any[] = [];


  constructor(private googleBooksService: GoogleBooksService) {}

  searchBooks() {
    this.googleBooksService.searchBooks(this.query).subscribe(data => {
      this.books = data.items;
    });
  }

  ngOnInit() {
    this.googleBooksService.getBestsellers().subscribe(data => {
      console.log(data.items)
      this.bestsellers = data.items;
    });
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <input type="text" class="form-control" [(ngModel)]="query" placeholder="Введите запрос">
      <button class="btn btn-primary mt-2" (click)="searchBooks()">Найти книги</button>
    </div>
  </div>
</div>


<div class="container" *ngIf="books && books.length > 0">
  <div class="row">
    <div class="col-md-4 mb-4" *ngFor="let book of books">
      <div class="card">
        <img [src]="book.volumeInfo.imageLinks?.thumbnail" class="card-img-top" alt="Book Cover">
        <div class="card-body">
          <h5 class="card-title">{{ book.volumeInfo.title }}</h5>
          <p class="card-text">Автор: {{ book.volumeInfo.authors }}</p>
          <p class="card-text">Страниц: {{ book.volumeInfo.pageCount }}</p>
          <p class="card-text">Год выпуска: {{ book.volumeInfo.publishedDate }}</p>
        </div>
      </div>
    </div>
  </div>
</div>
