import { Component, OnInit } from '@angular/core';
import { CheckFormService } from '../check-form.service';
import { FlashMessagesModule } from 'angular2-flash-messages';


@Component({
  selector: 'app-addtaskform',
  templateUrl: './addtaskform.component.html',
  styleUrls: ['./addtaskform.component.css']
})
export class AddtaskformComponent implements OnInit {

  name: string;
  description: string;

  constructor(private checkForm: CheckFormService){
    this.name = '';
    this.description = '';
  }
  ngOnInit(): void {  
  }

  addTaskClick(){
    if (this.checkForm.checkDescription(this.description)) {
      const task = {
        name: this.name,
        description: this.description,
      } 
      console.log('success');

      return false
    } else {
      console.log('Field "description" is required');
      return false;
    }
  }
}
