import { Component } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(public authService: AuthService) {}

  logout() {
    this.authService.token = null;
    this.authService.user = null;
    localStorage.clear();
  }
}
<header class="p-3 text-bg-dark">
  <div class="container">
    <div class="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">
      <a href="/" class="d-flex align-items-center mb-2 mb-lg-0 text-white text-decoration-none">
        <svg class="bi me-2" width="40" height="32" role="img" aria-label="Bootstrap"><use xlink:href="#bootstrap"></use></svg>
      </a>

      <ul class="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0">
        <li><a [routerLink]="['/']" class="nav-link px-2 fs-4 text-white">Home</a></li>
      </ul>

      <div class="text-end">
        <ng-container *ngIf="authService.token; else loginButtons">
          <span class="text-white me-2">{{ authService.user.name }}</span>
          <a (click)="logout()" class="btn btn-outline-light">Logout</a>
        </ng-container>
        <ng-template #loginButtons>
          <a [routerLink]="['/auth']" class="btn btn-outline-light me-2">Login</a>
          <a [routerLink]="['/reg']" class="btn btn-outline-warning">Sign-up</a>
        </ng-template>
      </div>
    </div>
  </div>
</header>
